LoadTable.WhereClause:
SqlOr(
if(IsBaseTree() and GetObjectKeyTable(from Object select current XObjectKey) <> 'AERole')then
   format("UID_Person in (select UID_Person from Person where UID_{0}= (select UID_{0} from {0} where XObjectKey = '{1}'))",
      GetObjectKeyTable(from Object select current XObjectKey),
      from Object select current XObjectKey
   ),
   format("UID_Person in (select UID_Person from Person{2}{3} where UID_{0}=(select UID_{0} from {0} where XObjectKey = '{1}'))",
      GetObjectKeyTable(from Object select current XObjectKey),
      from Object select current XObjectKey,
	  if(IsBaseTree())then "In" else "Has",
	  if((select current GetObjectKeyTable(XObjectKey)+"" from Object) = "Application")then "App" else (select current GetObjectKeyTable(XObjectKey) from object)
   )
)

