ActionSequence.Condition:
(0 < select count() from UNSGroup)
and
exists("person",
-- does the user have an active account or is one being requested?
format("
   UID_Person = '{0}' 
and 
   not exists(select 1 from PersonHasTSBAccountDef tab
      where tab.XOrigin > 0 and tab.UID_Person = '{0}' and exists ({1})
   )   
and
   not exists(select 1 from PersonWantsOrg pwo join itshoporg its on its.uid_itshoporg = pwo.uid_org join TSBAccountDef tab on tab.uid_accproduct = its.uid_accproduct
      where pwo.UID_PersonOrdered = '{0}' and pwo.OrderState  = 'OrderProduct' and exists({1})
   )
and
   not exists(select 1 from shoppingcartitem sci join TSBAccountDef tab on tab.uid_accproduct = sci.uid_accproduct
      where sci.UID_PersonOrdered = '{0}' and exists({1})
   )
", 
from PersonsForOrder select current UID_Person,
if(from unsgroup select top 1 UID_DPRNameSpace <> "ADS-DPRNameSpace-ADS")then(
   format("select 1 from tsbaccountdef t join unsroot r on r.uid_tsbaccountdef = t.uid_tsbaccountdef join unsgroup ug on ug.uid_unsroot = r.uid_unsroot where ug.uid_unsgroup = {0} and t.uid_accproduct > ' ' and t.UID_TSBAccountDef = tab.UID_TSBAccountDef",sqlformatvalue(from UNSGroup select top 1 UID_UNSGroup, "string", "NonUnicodeLiterals"))
)
else(
   format("select 1 from ADSDomain d where d.uid_ADSDomain in (select distinct uid_adsdomainmember from ADS_VGroupMemberValid_Group where UID_ADSGroupContainer = {0}) and d.UID_TSBAccountDef = tab.UID_TSBAccountDef", sqlformatvalue(from UNSGroup select top 1 UID_UNSGroup, "string", "NonUnicodeLiterals"))
)
)
)

Function.Expression:
{{
var p = new System.Collections.Generic.List<VI.DB.Entities.QueryParameter>();
p.Add(VI.DB.Entities.QueryParameter.Create<string>("uidaccproduct", uidAccProduct));
p.Add(VI.DB.Entities.QueryParameter.Create<string>("uidperson", uidPerson));
bool result;
using(var r = UserTable.GetLimitedSqlDataReader("QER_FGIPWOOrderPerson", p))
{
   r.Read();
   if( r.GetValue(0) is bool)
   {
      result = (bool)r.GetValue(0);
   }
   else
   {
       result = r.GetValue(0).ToString().Equals("1");
   }
}

@return result;
}}
