LoadTable.WhereClause:
sqlin(
from Dashboardinfo select current elementnamecolumnref,
"string",
from ElementNames select ElementName where ident_dialogdashboarddef = ( from dashboardinfo select current displayname)
)

Label.Text:
from dashboarddef select current
(if(aggregatefunction = "sum") then (translate("#LDS#The displayed value is the sum of the values calculated for the following objects")+":")
else if(aggregatefunction = "avg") then (translate("#LDS#The displayed value is the average of the values calculated for the following objects")+":"))

Column.Expression:
isnullorempty(
from elementnames select top 1 elementnamedisplay where (uid_dialogdashboarddef = ( select current uid_dialogdashboarddef from dashboardcontent) and elementname = ( select current elementname from dashboardcontent )),

translatecolumn(elementname, "dialogdashboardcontent", "elementname"))

from ReferencedObjects select top 1 displaylong() where key = ( 
IsNullOrEmpty(select current elementobjectkey from dashboardcontentagg, select current elementobjectkeynavigation from dashboardcontentagg)
)

isnullorempty(
from elementnameobjects select top 1 displaylong() where (uid_dialogdashboarddef = ( select current uid_dialogdashboarddef from elementnames) and propertyvalue = ( select current elementname from elementnames )),
elementname)
