LayoutObject.CodeLiteral:
IDbColumnAdapterFactory columnAdapterFactory = _appInfo.UnityContainer.Resolve<IDbColumnAdapterFactory>();

columnAdapterFactory.ColumnAdapterCreated += (s,e) =>
{
	// mark specific columns as having a relevant TimeOfDay

	var column = e.Column;
	var dbSourceColumn = column.DatabaseSourceColumn;
	if (dbSourceColumn == null)
		return;

	if ((dbSourceColumn.Table.Tablename.Equals("PersonWantsOrg")
		|| dbSourceColumn.Table.Tablename.Equals("ShoppingCartItem"))
		&& (dbSourceColumn.Columnname.Equals("ValidFrom")
		|| dbSourceColumn.Columnname.Equals("ValidUntil")))
	{
		column.ExtendedProperties[DateControl.DateControlTypePropertyKey] = CalendarType.DateTime.ToString();
	}
};

